/* ==UserStyle==
@name         Spotify Web - Light Theme
@description  A Light Theme for Spotify Web
@version      20250303.23.23
@namespace    https://userstyles.world/user/Lerinc
==/UserStyle== */

@-moz-document domain("open.spotify.com") {
    body  {
        filter: invert(100%) !important;
    }

    /* Site colors (inverted) */  
    .encore-dark-theme {
        --text-bright-accent: #e246ab;
        --text-negative: #0c8d80;
        --text-warning: #005bd4;
        --text-positive: #e1289f;
        --text-announcement: #b34c00;
        --essential-bright-accent: #e1289f;
        --essential-negative: #12d3c0;
        --essential-warning: #005bd4;
        --essential-positive: #e1289f;
        --essential-announcement: #b34c00;
    }
    ::selection { 
        background-color: #cc6602;
        color: #000;
    }
    .encore-dark-theme .encore-bright-accent-set, 
    .mWj8N7D_OlsbDgtQx5GW[role="menuitemradio"][aria-checked="true"],
    .KAZD28usA1vPz5GVpm63.RK45o6dbvO1mb0wQtSwq
    {
        filter: invert(100%) !important;
    }
    div[style*="--progress-bar-transform"]  {
        --is-active-fg-color: #e246ab;
    }

    /* Global variables */
    icon, 
    img, 
    video, 
    dialog, 
    div[style*="background-image"] {
        filter: invert(100%) !important;
    }

    /* Home WatchFeed */
    .KYeeT
    {
