  .docs-grille-gm3.docs-body-pageless .docos-anchoreddocoview:hover {background: var(--ddocs-surface-container);}

  .docs-grille-gm3 .kix-discussion-plugin>.docos-comments-pe>.docos-anchoreddocoview /* Comments along document */, 
  .docs-grille-gm3 .docs-docos-activity-sidebar.docos-comments-pe .docos-streamdocoview /* Comments sidebar piggybacking on this */ {
    // Background
    background-color: var(--ddocs-surface-container-low) !important; // Letting the grandest parent handle the background
    .docos-anchoredreplyview, .docos-anchoreddocoview-input-pane {
      background: none; // don’t duplicate backgrounds in child elements
    }
    .docos-anchoreddocoview-internal /* Using the 2nd grandest parent as a state layer */ {
      background: transparent;
      // transition: var(--off); // was causing white flashes on reload
    }
    &:hover .docos-anchoreddocoview-internal {
      background-color: var(--ddocs-on-surface-variant-hover);
      transition: var(--on);
    }
    &.docos-docoview-active .docos-anchoreddocoview-internal {
      background-color: var(--ddocs-on-surface-variant-focus) !important;
    }
    .docos-anchoreddocoview-minimized {
      background-color: var(--ddocs-surface-container-highest);
    }
    &.docos-minimized-view-mode:not(.docos-minimized-view):not(.docos-docoview-active) {
      padding-right: 0;
    }

    // Text colour
    :is(.docos-author, .docos-replyview-body) {
      color: var(--ddocs-on-surface);
    }
    :is(.docos-action-text, .docos-replyview-timestamp, .docos-input-acl-fixer-message, .docos-input-assignment-message, .docos-input-at-reply-message, .docos-input-static-message) {
      color: var(--ddocs-on-surface-variant);
    }
    .docos-replyview-body {
      a {color: var(--ddocs-primary)}
      span {color: var(--ddocs-on-surface-variant) !important}
    }

    // Other stuff
    .docos-replyview-resolve-button, .docos-accept-suggestion /* ✓ button */ {
      border-radius: 100%;
      transition: var(--off);
      &.jfk-button-hover {
        background-color: var(--ddocs-primary-hover);
        transition: var(--on);
      }
      &.jfk-button-focused {
        background-color: var(--ddocs-primary-focus);
      }
      .docs-icon {
        background-color: var(--ddocs-primary);
        clip-path: path("m9.55 18-5.7-5.7 1.425-1.425L9.55 15.15l9.175-9.175L20.15 7.4Z");
      }
      .docs-icon-img {
        display: none;
      }
    }
    &.docos-anchoreddocoview .docos-accept-suggestion .docs-icon {
      margin-top: 4px;
    }
    .docos-docomenu-dropdown /* ⋮ button */, .docos-reject-suggestion /* × button */ {
      border-radius: 100%;
      transition: var(--off);
      &:hover {
        background-color: var(--ddocs-on-surface-variant-hover);
        transition: var(--on);
      }
      &:focus {
        background-color: var(--ddocs-on-surface-variant-focus);
      }
      .docs-icon {
        filter: var(--white-icons);
      }
    }

    .docos-replyview-emoji-controls .docs-material-button /* Add emoji reaction floating button */ {
      background-color: var(--ddocs-surface-container-highest);
      border-color: transparent;
      .docs-material-button-content {
        transition: var(--off);
      }
      &.docs-material-button-hover .docs-material-button-content {
        background-color: var(--ddocs-on-surface-variant-hover);
        transition: var(--on);
      }
      &.docs-material-button-focused .docs-material-button-content {
        background-color: var(--ddocs-on-surface-variant-focus);
      }
      .docs-material-button-ripple-element {
        background-color: var(--ddocs-on-surface-variant-pressed);
      }
      .docs-icon {
        filter: var(--white-icons);
      }
    }

    .docos-collapsible-replyview /* Tall comments with “Show more/less” button */ {
      &.docos-collapsed-replyview .docos-replyview-body {
        height: calc(100% - 26px);
        overflow: hidden;
      }
      .docos-show-more, .docos-show-less {
        background: none !important;
        color: var(--ddocs-primary);
      }
    }

    .docos-replyview-emojis .docs-material-button-flat-primary /* Emoji reactions */ {
      border: 1px solid var(--ddocs-outline);
      padding: 0 5px;
      &.docs-material-button-disabled {
        background: none;
      }
      &:hover {
        background-color: var(--ddocs-on-surface-hover);
      }
      &:focus {
        background-color: var(--ddocs-on-surface-focus);
      }
      .docs-material-button-ripple-element {
        background-color: color-mix(in srgb, var(--ddocs-on-surface-variant), transparent 68%);
      }

      .docos-emoji-counter-button-count {
        color: var(--ddocs-on-surface-variant);
      }
      &.docos-emoji-counter-button-user-involved {
        &:hover {
          background-color: var(--ddocs-primary-hover);
        }
        &:focus {
          background-color: var(--ddocs-primary-focus);
        }
        .docs-material-button-ripple-element {
          background-color: color-mix(in srgb, var(--ddocs-primary), transparent 68%);
        }
        .docos-emoji-counter-button-count {
          color: var(--ddocs-primary);
        }
      }
    }

    .docos-showrepliesbutton /* “Show # more replies” button */ {
      &::before, &::after {
        background-color: var(--ddocs-outline-variant);
        content: ""; // Using pseudoelements to make the divider line. Googlers simply made one line and covered it with a box in the middle so it wouldn’t cross the text button, but that would’ve meant the box had to change background colour with the rest of the comment. I think it’s easier to actually make separate lines on either side of the button.
        flex-grow: 1; // This makes the lines as wide as possible, given the width of the button
        height: 0.5px;
      }
      .docos-showrepliesbutton-collapsed {
        width: max-content;
      }
      .docos-showrepliesbutton-line {
        display: none;
      }
      .docos-showrepliesbutton-collapsed-internal-container /* Said box that covers the divider line */ {
        background: none;
      }
      .docos-showrepliesbutton-collapsed-internal {
        background-color: transparent;
        color: var(--ddocs-on-surface-variant);
        transition: var(--off);
        user-select: none;
        &:hover {
          background-color: var(--ddocs-primary-hover);
        }
        &:active {
          background-color: var(--ddocs-primary-pressed);
        }
      }
    }
    &:hover .docos-showrepliesbutton-collapsed-internal {
      color: var(--ddocs-primary);
      transition: var(--on);
    }

    .docos-input /* Type a comment, reply, or edit */ {
      .docos-input-textarea {
        background: none;
        border-color: var(--ddocs-outline);
        caret-color: var(--ddocs-primary);
        color: var(--ddocs-on-surface);
        outline: none !important;
        transition: var(--off);
        &:empty::before /* Googlers used this as the placeholder */ {
          color: var(--ddocs-on-surface-variant);
        }
        &:hover {
          border-color: var(--ddocs-on-surface-variant);
          transition: var(--on);
        }
        &:focus {
          border-color: var(--ddocs-primary);
          box-shadow: inset 0 0 0 1px var(--ddocs-primary);
        }
      }
      .jfk-button.docos-input-cancel {
        color: var(--ddocs-primary) !important;
        transition: var(--off);
        &.jfk-button-hover {
          background-color: var(--ddocs-primary-hover);
          transition: var(--on);
        }
        &.jfk-button-focused {
          background-color: var(--ddocs-primary-focus);
        }
      }
      .jfk-button.docos-input-buttons-post {
        background-color: var(--ddocs-primary);
        box-shadow: none;
        color: var(--ddocs-on-primary) !important;
        transition: var(--off);
        &.jfk-button-disabled {
          background-color: var(--ddocs-on-surface-disabled);
          color: color-mix(in srgb, var(--ddocs-on-surface), transparent 62%) !important;
        }
        &.jfk-button-hover {
          background-color: var(--ddocs-on-primary-hover);
          transition: var(--on);
        }
        &.jfk-button-focused {
          background-color: var(--ddocs-on-primary-focus);
        }
      }
      .docos-input-assignment-panel /* When you mention someone by @email */ {
        border-bottom-color: var(--ddocs-outline-variant);
        .docs-material-gm-checkbox-checked {
          background-color: var(--ddocs-primary);
          border-color: var(--ddocs-primary);
        }
        .docos-input-assignee-text {
          color: var(--ddocs-on-surface);
        }
      }
    }

    .docs-minimizeddocoview-avatar {
      border-color: transparent;
    }
    .docos-replyview-reply-count /* “# replies” (when hovering on minimized comment) */ {
      background-color: transparent;
      color: var(--ddocs-primary);
      transition: var(--off);
      &:hover {
        background-color: var(--ddocs-primary-hover);
        transition: var(--on);
      }
      &:focus {
        background-color: var(--ddocs-primary-focus);
      }
    }
  }

  body.docs-grille-gm3 .kix-discussion-plugin-vertical .docos-calltoactionview /* Pageless mode ad */ {
