  // “Page setup” dialog
  .pageSetupDialogTabPrimary.javascriptMaterialdesignGm3WizTabsPrimary-tab /* Pages/Pageless tabs */ {
    background: none;
  }
  .pageSetupDialogColorPickerDropdown button.javascriptMaterialdesignGm3WizButtonDropdownOutlined-button:disabled circle /* Page colour picker swatch when disabled */ {
    opacity: 38%;
  }
  .pageSetupDialogColorPickerDropdown .appsElementsColorPickerColorEditor-color-editor__section /* ‘>’ between the color preview and its hex code */ {
