/* ==UserStyle==
@name           Old Expando buttons for Reddit
@namespace      aubymori
@version        1.0.0
@description    Restores the old Expando buttons on the old Reddit design
@author         aubymori
@preprocessor   less
@var            checkbox defaultCursor "Use default cursor instead of pointer" 1
==/UserStyle== */

@-moz-document domain("reddit.com") {
    .expando-button {
        background-image: url("//www.redditstatic.com/sprite-reddit.5kxTB7FXse0.png") !important;

        & when (@defaultCursor = 1) {
            cursor: default !important;
        }

        &.selftext, &.crosspost {
            &.collapsed {
                background-position: -29px -1442px;

                &:hover {
                    background-position: 0 -1442px;
                }
            }

            &.expanded {
                background-position: -87px -1442px;

                &:hover {
                    background-position: -58px -1442px;
                }
            }
        }

        &.image, &.video, &.video-muted, &.gallery {
            &.collapsed {
                background-position: 0 -1471px;

                &:hover {
                    background-position: -116px -1442px;
                }
            }

            &.expanded {
                background-position: -58px -1471px;

                &:hover {
                    background-position: -29px -1471px;
                }
            }
        }
    }
}

